Z[{1, 2}] := d1: Z[{2, 3}] := d2: Z[{3, 4}] := d3: Z[{1, 3}] := d1+d2: Z[{1, 4}] := d1+d2+d3: Z[{2, 4}] := d2+d3:
dist := proc (myinput, k) local i, myseq, x; x := 0; myseq := [7, op(myinput), 7]; for i to 7 do
if 7<= myseq[i] and 7<= myseq[i+1] then x:=x elif 7<= myseq[i] and myseq[i+1]<7 then x:=x+Z[{myseq[i+1],k}] elif myseq[i]<7 and 7<= myseq[i+1] then x:=x+Z[{myseq[i],k}]
elif myseq[i]<7 and myseq[i+1]<7 then x:=x+Z[{myseq[i+1],myseq[i]}] else RETURN(ERROR) fi:
od: x: end:
checker := proc (my720) local k, flag, x, y, goodlist, temp; goodlist := NULL; for k to 720 do
temp := Matrix([seq(my720[k][t], t = 1 .. 6)]); flag := 0; for x to 4 do for y to 5 do
if abs(temp[x][y]-temp[x][y+1])=10 then flag:=1 fi:
od: od: for x to 4 do
if dist([seq(temp[x,k],k = 1..6)],x)<>2*(d1+d2+d3) then flag := 1 fi:
od: if flag = 0 then goodlist := goodlist, temp: fi:
od: goodlist: end:
