10 Gender = R1.groupdict()[’gender’]; 11 is-starting→ 1; 12 else 13 if R2.match(sentence) then 14 Age = int(R3.groupdict()[’age’]);
15 else 16 Age = Null;
17 if R3.match(sentence) then 18 Gender = int(R3.groupdict()[’gender’]);
19 else 20 Gender = Null;
21 if Age 6= Null && Gender 6= Null then 22 is-starting→ 1;
23 if is-starting then 24 n += 1; 25 S C n = index of the sentence;
26 N = n;
