# This calculate the leftmost intercept line for a set of points given in arrays X and
Y
leftmost_intercept <- function(slope, X, Y) {
best_intercept <- Inf for (i in 1:length(X)) {
x <- X[i] y <- Y[i] intercept <- y - slope*x if (intercept < best_intercept)
best_intercept <- intercept
} best_intercept
}
# This calculates the leftmost intercept line for a set of points given in arrays X
and Y.
# But this one returns the points leftmost_intercept_xy <- function(slope, X, Y) {
best_intercept <- Inf for (i in 1:length(X)) {
x <- X[i] y <- Y[i] intercept <- y - slope*x if (intercept < best_intercept) {
best_intercept <- intercept bestx <- x besty <- y
}
} c(bestx,besty)
}
