rcca_fit <- function(x,y,mx,my,sx,sy) {
w <- matrix(sx*rnorm(ncol(x)*mx),ncol(x)) m <- matrix(sy*rnorm(ncol(y)*my),ncol(y)) C <- cancor(cos(x%*%w),cos(y%*%m)) list(a=C$xcoef,b=C$ycoef,w=w,m=m)
}
rcca_eval <- function(rcca,x,y) list(x=cos(x%*%rcca$w)%*%rcca$a, y=cos(y%*%rcca$m)%*%rcca$b) }
rpca_fit <- function(x,m,s) { w <- matrix(s*rnorm(ncol(x)*m),ncol(x)) list(w=w,pca=prcomp(cos(x%*%w))) }
rpca_eval <- function(rpca,x) predict(rpca$pca,cos(x%*%rpca$w)) }
